let btnNext=document.querySelector(".arrow-next"),btnPrev=document.querySelector(".arrow-prev");document.addEventListener("DOMContentLoaded",(function(){var e=new Splide(".splide",{type:"loop",perPage:4,pagination:!1,perMove:1,arrows:!1,autoWidth:!1,breakpoints:{1080:{perPage:3},768:{perPage:2},480:{perPage:1}}});e.mount(),btnNext.addEventListener("click",(t=>{e.go("+1")})),btnPrev.addEventListener("click",(t=>{e.go("-1")}))}));const smoothLinks=document.querySelectorAll('a[href^="#"]');for(let e of smoothLinks)e.addEventListener("click",(function(t){t.preventDefault();const o=e.getAttribute("href");document.querySelector(o).scrollIntoView({behavior:"smooth",block:"start"})}));